import { useSelector } from 'react-redux'
import {LOAD_NEWS, WORD_SEARCH} from '../Store/Type'
const initState = {
 cards:[],
 word:'',
 lang:''
}

export const SearchReducer = (state =initState, action) => {
    switch (action.type) {
        case LOAD_NEWS: return {...state, cards: [...state.cards, {...action.data}]}
        case WORD_SEARCH: return {...state, word: action.word}
        default: return state
    }

}
export const ws = (word) => {
    return {type: WORD_SEARCH, word}
    
} 
export const loadnews = (data) => {
    return {type:LOAD_NEWS, data}
}
