import  React, {useState, useEffect }from 'react';
import { View, Text, Button, Image,TextInput, StyleSheet  } from 'react-native';
import {useDispatch, useSelector} from 'react-redux'
import {Api} from '../Api/Api'
import SelectDropdown from 'react-native-select-dropdown'
import {ws, changeLang} from '../Reducer/SearchReducer'

export const MainScreen =({navigation}) => {
  MainScreen.navigationOptions={
    headerTitle:'Новостной поиск'
  }

const onSelect2 = (selectedItem, index) => {
 // console.log(selectedItem, index)// https://reactnativeexample.com/a-highly-customized-dropdown-select-picker-menu-for-react-native/
  dispatch(changeLang(index))
}
//const countries = ["RU", "ENG"]

    const [valueText, SetValue] = useState('') 
    const dispatch = useDispatch()
    const goToPage =() =>{  
      dispatch(ws(valueText))
        navigation.push('ResultScreen')
        SetValue("")
    }  
    
    return ( 
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
  
     <TextInput
        value = {valueText}
        style={styles.input}
       onChangeText={(value)=>SetValue(value)}
        />
       
    <Button
    style={styles.button}
      title="Поиск"
      onPress={goToPage}
    />
    <TouchableOpacity><Text>О программе</Text></TouchableOpacity>
  </View>)
}

const styles = StyleSheet.create({
  input: {
    height: 50,
    width: 300,
    margin: 5,
    borderWidth: 2,
    borderRadius:10,
    
    borderColor: "#00BFFF",
    fontSize: 20,
    shadowOffset: { width: 0, height: 1},
    shadowOpacity: 0.20,
    shadowRadius: 0.5,
    elevation: 2
  },
  button: {
    padding: 20
    
  },
  Sel: {
    borderWidth: 2,
    borderColor: "#00BFFF",
    width:80,
    borderRadius:20
  }
});
